<?php

namespace Teo\ProductBundle\Entity;

use Doctrine\ORM\EntityRepository;

/**
 * CategoryRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class CategoryRepository extends EntityRepository
{
    public function findCategoryByTag($tag)
    {
        $q = $this->getQueryForTag($tag);
        return $q->getQuery()->getOneOrNullResult();
    }

    public function findCategoriesByTag($tag)
    {
        $q = $this->getQueryForTag($tag);
        return $q->getQuery()->getResult();
    }

    public function findOrdered()
    {
        $q = $this->createQueryBuilder('c');
        $q
            ->orderBy('c.position', 'ASC')
            ;
        return $q->getQuery()->getResult();
    }

    public function getQueryForTag($tag)
    {
        $q = $this->createQueryBuilder('c');
        $q
            ->innerJoin('c.tags', 't')
            ->andWhere('t.name = :name')
            ->setParameter('name', $tag)
            ->orderBy('c.position', 'ASC')
            ;
        return $q;
    }

    public function reorder($ids)
    {
        if (count($ids) == 0) {
            return;
        }

        $counter = 0;
        foreach ($ids as $id) {
            $this->find($id)->setPosition($counter);
            $counter++;
        }

        $this->_em->flush();
    }

    // public function find($id)
